def shortest(journalists)
    puts "Le handle le plus court est :"

    journalists.sort_by!(&:length)
    puts journalists[0]
    print ">"
end


def five_letter(journalists)
    puts "Combien y-a-t'il de handles contenant 5 caractères ?"

    five1 = journalists.select{|journalists|journalists.size==6}
    five = five1.count
    puts "Il y a #{five} handles de 5 caratères."
    print ">"
end


def maj(journalists)
    puts "Combien commencent par une majuscule ?"
    puts journalists.count {|journalists| journalists =~ /^@[A-Z]/}
end


def alphabet(journalists)
    puts "Les voicis maintenant dans l'ordre alphabétique :"
    print ">"
    puts journalists.sort
end


def handle_size(journalists)
    puts "Les voicis maintenant par ordre de taille , allant du plus petit au plus grand :"
    print ">"
    puts journalists.sort_by!(&:length)
end


def position(journalists)
    puts "Quel est la position de '@epenser' dans l'array ?"
    print ">"
    puts "'@epenser est à la position :"
    puts journalists.index("@epenser")
end


def handle_3(journalists)
    puts "Voici une répartition en les classants par leur tailles , des pseudos contenant maximum 3 caractères :"
    three1 = journalists.select{|journalists|journalists.size<=4}
    puts three1.sort_by(&:length)
end
    

def perform
    journalists = nombre
    shortest(journalists)
    five_letter(journalists)
    maj(journalists)
    alphabet(journalists)
    handle_size(journalists)
    position(journalists)
    handle_3(journalists)
end


